package wd;

public class GroceryList {

	public static void main(String[] args) {
		String[] shoppingItems = {"Milk", "Eggs", "Rice", "Butter"};
		System.out.println("Grocery Shopping List:");
		for (String item : shoppingItems) {
			System.out.println(" " + item);
		}
		System.out.println("All items listed. Happy shopping!");
	}
}
		
				
